import React from 'react';
// import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends React.Component {

  state = {
    cards: []
  }

  componentDidMount(){
    console.log('cDM is running')
    //axios to pull data from github
    axios.get('https://api.github.com/users/Rae-Glazier')
      .then(res => {
        console.log(res);

        this.setState({ cards: res.data });
      });
  }

  render() {

    return (
      <div className="App">
        <header className="App-header">
         <h1> Github User Card</h1>
        </header>

        <div className='my-card'>
          <p>Username: {this.state.cards.login} </p>
          <p>Name: {this.state.cards.name}</p>
          <p>Location: {this.state.cards.location}</p>
          <p>Bio: {this.state.cards.bio}</p>
        </div>




      </div>
    );

  }
}

export default App;
